/*global google*/
import React, { Component } from "react";
import {
  withGoogleMap,
  withScriptjs,
  GoogleMap,
  DirectionsRenderer
} from "react-google-maps";
import DirectionInstructions from './Instructions.js'





class Map extends Component {
  state = {
    directions: [],
    directionsLocal: [],
    lat:'hello',
    lng:0
  };

  getpositon=()=>{

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(getpositon);

          if( this.lng1 != undefined && this.lat1 != undefined )  {

            this.setState({
                lat:this.lat1,
                lng:this.lng1 
            })
          }
    }

    // function displayLocationInfo(position){
      const lng1 = position.coords.longitude;
      const lat1 = position.coords.latitude;

      console.log(`longitude: ${ lat1 } | latitude: ${ lng1 }`);
      return  lng1,lat1
      // console.log('helo'+this.state.lat)


    // }
    //  displayLocationInfo()

  }

  componentDidMount=()=> {
    let origin =  this.getpositon();
    let destination = { lat: 41.756795, lng: -78.954298 };

    console.log(origin)








    const directionsService =  new google.maps.DirectionsService();
    //   if(lng == '' ){

    //   }


    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: google.maps.TravelMode.DRIVING
      },
      (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          this.setState({
            directions: result

          });
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );

  }

  render() {
    const GoogleMapExample = withGoogleMap(props => (
      <GoogleMap
        defaultCenter={{ lat: 40.756795, lng: -73.954298 }}
        defaultZoom={13}
      > 
        <DirectionsRenderer directions={this.state.directions}/>

      </GoogleMap>
    ));
    // console.log(this.state.directions)

    // if(this.state.directions.routes ){


    //     console.log('routes ',this.state.directions.routes[0].legs[0].steps[0].instructions)

    // }

    // {console.log(this.state.directions.routes)}
    return (
      <div>




        <GoogleMapExample          containerElement={<div style={{ height: `500px`, width: "500px" }} />}
          mapElement={<div style={{ height: `100%` }} />}
        />
       <div>
       {this.state.directions.routes ? this.state.directions.routes[0].legs[0].steps.map((elemen)=> (<div> <p dangerouslySetInnerHTML={{ __html: elemen.instructions }}/> </div>)) :''}
       </div>

      </div>
    );
  }
}

export default Map;
