import React, { Component } from "react";


class DestinationForm extends Component {

    state = {
        destinationAddress:''
    }
    handleChange=(event)=>{
        console.log(event.target.value);
        this.setState({[event.target.id] :event.target.value})
    }

    handleSubmit =()=>{
        event.preventDefault();
        this.setState({
            destinationAddress: this.state.price,
        })
    }

    render(){
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                <lable htmlFor='destinationAddress' >Destination Address</lable>
                <input type="text" value={this.state.destinationAddress} onChange={this.handleChange} id='destinationAddress' />
                </form>
            </div>
        )
    }

}

export default DestinationForm;
