/*global google*/
import React, { Component } from "react";
import {
  withGoogleMap,
  withScriptjs,
  GoogleMap,
  DirectionsRenderer
} from "react-google-maps";
import Geocode from "react-geocode";





 Geocode.setApiKey("AIzaSyCTIuglr0yoyg7N3YinUj0xEeKmQCA8VT0");
 Geocode.enableDebug();


class Map extends  React.Component {
  state = {
    directions: [],
    directionsLocal: [],
    destination: '',
    latOrigin:0,
    lngOrigin:0,
    latDestin:0,
    lngDestin:0,
    destinationAddress:'',

  };

  //   handleChange=(event)=>{
  //   console.log(event.target.value);
  //   this.setState({[event.target.id] :event.target.value})
  //  }

    handleSubmit =()=>{
      event.preventDefault();
      this.setState({
          destinationAddress: this.state.destinationAddress,
      })
    }

    geoDestiny = ()=>{

      Geocode.fromAddress("california").then(
        response => {
          let { lat, lng } = response.results[0].geometry.location;
          this.setState({
            latDestin:lat,
            lngDestin:lng,
          })
          console.log(this.state.latDestin, this.state.lngDestin);
          this.getpositon();
        },
        error => {
          console.error(error);
        }
      );
    }

     displayLocationInfo=(position)=>{

        let lng2 = position.coords.longitude;
        let lat2 = position.coords.latitude;
        console.log(`longitude: ${ lat2 } | latitude: ${ lng2 }`);
       this.setState({
        latOrigin: lat2,
        lngOrigin:lng2
       })
       console.log('disply'+this.state.lat)

       this.loadMap()

     }



      getpositon=()=>{

        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(this.displayLocationInfo);

        } 

      }

      loadMap=()=>{
        console.log(`origin lat: ${this.state.latOrigin}, lng: ${this.state.lngOrigin} `)
        console.log(`Destino lat:  ${this.state.latDestin}, lng: ${this.state.lngDestin} `)

        let origin = { lat: this.state.latOrigin, lng: this.state.lngOrigin };
        let destination = { lat:  this.state.latDestin, lng: this.state.lngDestin };
        const directionsService =  new google.maps.DirectionsService();
        directionsService.route(
          {
            origin: origin,
            destination: destination,
            travelMode: google.maps.TravelMode.DRIVING
          },
          (result, status) => {
            if (status === google.maps.DirectionsStatus.OK) {
              this.setState({
                directions: result

              });
            } else {
              console.error(`error fetching directions ${result}`);
            }
          }
        );
      }

  componentDidMount = ()=> {
    this.geoDestiny()


    console.log('component'+this.state.lat)


  }

  render() {
    const GoogleMapExample = withGoogleMap(props => (
      <GoogleMap
        defaultCenter={{ lat: 40.756795, lng: -73.954298 }}
        defaultZoom={13}
      > 
        <DirectionsRenderer directions={this.state.directions}/>

      </GoogleMap>
    ));

    return (
      <div>
        <GoogleMapExample          containerElement={<div style={{ height: `500px`, width: "500px" }} />}
          mapElement={<div style={{ height: `100%` }} />}
        />
       <div>
       {this.state.directions.routes ? this.state.directions.routes[0].legs[0].steps.map((elemen)=> (<div> <p dangerouslySetInnerHTML={{ __html: elemen.instructions }}/> </div>)) :''}
       </div>


         <div>
         <lable htmlFor='description' className='col-sm-1 col-form-label' >URL:</lable>
            <div class="col-sm-11">
                <input type='url'  required value={this.state.url}  className='form-control' id='url' onChange={this.handleChange}/>
            </div>
         </div>
      </div>
    );
  }
}

export default Map;
