import React, { Component } from "react";

class AddPlaces extends Component {
  state = {
    Places: [],
    nick_name: "",
    addrees: "",
    img: "",
  };

  handleChange = (event) => {
    console.log(event.target.value);
    // this.state.value = event.target.value;
    this.setState({ [event.target.id]: event.target.value });
  };

  //   componentDidMount() {
  //     this.getPlaylist();
  //   }

  //   getPlaylist = () => {
  //     fetch("http://localhost:3000/playlists")
  //       .then((response) => response.json())
  //       .then((json) => this.setState({ playlist: json }))
  //       // .then(playlist => console.log(this.state.playlist))
  //       .catch((error) => console.error(error));
  //   };

  handleSubmit = (event) => {
    event.preventDefault();
    fetch("http://localhost:3000/places", {
      body: JSON.stringify({
        place: this.state.nick_name,
        addrees: this.state.addrees,
        img: this.state.img,
      }),
      method: "POST",
      headers: {
        Accept: "application/json, text/plain, */*",
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((newTodo) => {
        this.setState({
          Places: [newTodo, ...this.state.Places],
          nick_name: "",
          addrees: "",
          img: "",
        });
      });
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label htmlFor="nick_name">nick_name</label>
        <input
          type="text"
          value={this.state.nick_name}
          onChange={this.handleChange}
          id="nick_name"
        />
        <br />
        <label htmlFor="addrees">address</label>
        <input
          type="text"
          value={this.state.addrees}
          onChange={this.handleChange}
          id="addrees"
        />
        <br />
        <label htmlFor="img">img</label>
        <input
          type="text"
          value={this.state.img}
          onChange={this.handleChange}
          id="img"
        />
        <input type="submit" />
      </form>
    );
  }
}

export default AddPlaces;
