/*global google*/
import React, { Component } from "react";
import {
  withGoogleMap,
  withScriptjs,
  GoogleMap,
  DirectionsRenderer
} from "react-google-maps";





class Map extends Component {
  state = {
    directions: {}
  };

  componentDidMount() {
    const directionsService = new google.maps.DirectionsService();

    const origin = { lat: 40.756795, lng: -73.954298 };
    const destination = { lat: 41.756795, lng: -78.954298 };

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: google.maps.TravelMode.DRIVING
      },
      (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          this.setState({
            directions: result
          });
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );

  }

  render() {
    const GoogleMapExample = withGoogleMap(props => (
      <GoogleMap
        defaultCenter={{ lat: 40.756795, lng: -73.954298 }}
        defaultZoom={13}
      >
        <DirectionsRenderer directions={this.state.directions}/>
        {/* <DirectionsRoute directions={this.state.directions} /> */}
      </GoogleMap>
    ));

    return (
      <div>
        {console.log(this.state.directions)}
        {console.log(this.state.directions[0].routes)}
        {/* {this.state.directions.routes.legs.map((elemen)=>{
            return(
            <h3>{elemen}</h3>
            )
        })} */}
        <GoogleMapExample          containerElement={<div style={{ height: `500px`, width: "500px" }} />}
          mapElement={<div style={{ height: `100%` }} />}
        />


      </div>
    );
  }
}

export default Map;
